import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
    state: {
        tasks: [],
        lists: []
    },
    getters: {
        tasks(state) {
            return state.tasks
        },
        lists(state) {
            return state.lists
        }
    },
    mutations: {
        addTask(state, task) {
            state.tasks = task
        }
    },
    actions: {
        addTask({commit}, task) {
            commit('addTask', task);
        }
    }
})
